import React, { useState, useEffect } from 'react';
import EmailService from './EmailService';
import './ModernUI.css';

const EmailNotifications = () => {
  const [emailHistory, setEmailHistory] = useState([]);
  const [selectedQuiz, setSelectedQuiz] = useState('');
  const [quizzes, setQuizzes] = useState([]);
  const [students, setStudents] = useState([]);
  const [sending, setSending] = useState(false);
  const [message, setMessage] = useState('');

  useEffect(() => {
    loadData();
  }, []);

  const loadData = () => {
    const savedQuizzes = JSON.parse(localStorage.getItem('quizzes') || '[]');
    const savedStudents = JSON.parse(localStorage.getItem('students') || '[]');
    const history = EmailService.getEmailHistory();

    setQuizzes(savedQuizzes);
    setStudents(savedStudents);
    setEmailHistory(history);
  };

  const sendQuizReminders = async () => {
    if (!selectedQuiz) {
      setMessage('Please select a quiz first');
      return;
    }

    setSending(true);
    setMessage('Sending reminders...');

    const quiz = quizzes.find(q => q.id.toString() === selectedQuiz);
    const emailPromises = students.map(student => 
      EmailService.sendQuizReminder(`${student.username}@example.com`, quiz)
    );

    try {
      const results = await Promise.allSettled(emailPromises);
      const successful = results.filter(r => r.status === 'fulfilled' && r.value.success).length;
      setMessage(`Sent ${successful} reminder emails successfully!`);
      loadData(); // Refresh email history
    } catch (error) {
      setMessage('Failed to send reminders');
    } finally {
      setSending(false);
    }
  };

  const sendNewQuizNotification = async () => {
    if (!selectedQuiz) {
      setMessage('Please select a quiz first');
      return;
    }

    setSending(true);
    setMessage('Sending notifications...');

    const quiz = quizzes.find(q => q.id.toString() === selectedQuiz);

    try {
      const results = await EmailService.sendNewQuizNotification(quiz);
      const successful = results.filter(r => r.status === 'fulfilled' && r.value.success).length;
      setMessage(`Sent ${successful} new quiz notifications!`);
      loadData();
    } catch (error) {
      setMessage('Failed to send notifications');
    } finally {
      setSending(false);
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleString();
  };

  const getEmailTypeIcon = (type) => {
    switch (type) {
      case 'template_quiz_reminder': return '⏰';
      case 'template_quiz_results': return '📊';
      case 'template_new_quiz': return '📢';
      default: return '📧';
    }
  };

  return (
    <div className="modern-card">
      <h2 style={{ color: 'var(--text-primary)', marginBottom: '1.5rem' }}>
        📧 Email Notifications
      </h2>

      {/* Email Controls */}
      <div style={{ marginBottom: '2rem' }}>
        <div className="modern-form-group">
          <label className="modern-form-label">Select Quiz:</label>
          <select 
            className="modern-form-select"
            value={selectedQuiz}
            onChange={(e) => setSelectedQuiz(e.target.value)}
          >
            <option value="">Choose a quiz...</option>
            {quizzes.map(quiz => (
              <option key={quiz.id} value={quiz.id}>{quiz.title}</option>
            ))}
          </select>
        </div>

        <div style={{ display: 'flex', gap: '1rem', flexWrap: 'wrap' }}>
          <button 
            className="modern-btn modern-btn-primary"
            onClick={sendQuizReminders}
            disabled={sending || !selectedQuiz}
          >
            {sending ? '⏳ Sending...' : '⏰ Send Reminders'}
          </button>

          <button 
            className="modern-btn modern-btn-secondary"
            onClick={sendNewQuizNotification}
            disabled={sending || !selectedQuiz}
          >
            {sending ? '⏳ Sending...' : '📢 Notify New Quiz'}
          </button>
        </div>

        {message && (
          <div style={{
            marginTop: '1rem',
            padding: '0.75rem',
            borderRadius: '6px',
            backgroundColor: message.includes('Failed') ? '#fee2e2' : '#d1fae5',
            color: message.includes('Failed') ? '#dc2626' : '#065f46',
            border: `1px solid ${message.includes('Failed') ? '#fecaca' : '#a7f3d0'}`
          }}>
            {message}
          </div>
        )}
      </div>

      {/* Email Statistics */}
      <div style={{ marginBottom: '2rem' }}>
        <h3 style={{ color: 'var(--text-primary)' }}>Email Statistics</h3>
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(150px, 1fr))', gap: '1rem' }}>
          <div style={{ 
            padding: '1rem', 
            backgroundColor: '#eff6ff', 
            borderRadius: '8px',
            textAlign: 'center'
          }}>
            <div style={{ fontSize: '1.5rem', fontWeight: 'bold', color: '#1e40af' }}>
              {emailHistory.length}
            </div>
            <div style={{ fontSize: '0.875rem', color: '#64748b' }}>Total Emails</div>
          </div>

          <div style={{ 
            padding: '1rem', 
            backgroundColor: '#f0fdf4', 
            borderRadius: '8px',
            textAlign: 'center'
          }}>
            <div style={{ fontSize: '1.5rem', fontWeight: 'bold', color: '#16a34a' }}>
              {students.length}
            </div>
            <div style={{ fontSize: '0.875rem', color: '#64748b' }}>Active Students</div>
          </div>

          <div style={{ 
            padding: '1rem', 
            backgroundColor: '#fef3c7', 
            borderRadius: '8px',
            textAlign: 'center'
          }}>
            <div style={{ fontSize: '1.5rem', fontWeight: 'bold', color: '#d97706' }}>
              {emailHistory.filter(e => e.timestamp > new Date(Date.now() - 24*60*60*1000).toISOString()).length}
            </div>
            <div style={{ fontSize: '0.875rem', color: '#64748b' }}>Last 24h</div>
          </div>
        </div>
      </div>

      {/* Email History */}
      <div>
        <h3 style={{ color: 'var(--text-primary)' }}>Recent Email Activity</h3>
        {emailHistory.length === 0 ? (
          <p style={{ color: 'var(--text-secondary)', textAlign: 'center', padding: '2rem' }}>
            No emails sent yet. Send your first notification above!
          </p>
        ) : (
          <div style={{ maxHeight: '300px', overflowY: 'auto' }}>
            {emailHistory.slice(-10).reverse().map((email, index) => (
              <div key={index} style={{
                display: 'flex',
                alignItems: 'center',
                padding: '0.75rem',
                marginBottom: '0.5rem',
                backgroundColor: '#f8fafc',
                borderRadius: '6px',
                border: '1px solid #e2e8f0'
              }}>
                <span style={{ fontSize: '1.2rem', marginRight: '0.75rem' }}>
                  {getEmailTypeIcon(email.type)}
                </span>
                <div style={{ flex: 1 }}>
                  <div style={{ fontWeight: '500', color: 'var(--text-primary)' }}>
                    {email.email}
                  </div>
                  <div style={{ fontSize: '0.875rem', color: 'var(--text-secondary)' }}>
                    {formatDate(email.timestamp)}
                  </div>
                </div>
                <span style={{
                  padding: '0.25rem 0.5rem',
                  borderRadius: '12px',
                  fontSize: '0.75rem',
                  backgroundColor: '#dcfce7',
                  color: '#166534'
                }}>
                  {email.status}
                </span>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default EmailNotifications;
