import React, { useState } from 'react';
import axios from 'axios';

const QuizForm = ({ onQuizCreated }) => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [timeLimit, setTimeLimit] = useState('');
  const [message, setMessage] = useState('');
  const [errors, setErrors] = useState({});

  const validateForm = () => {
    const newErrors = {};
    if (!title || title.length < 3 || title.length > 100) {
      newErrors.title = 'Quiz title must be between 3 and 100 characters.';
    }

    const parsedTimeLimit = parseInt(timeLimit);
    if (isNaN(parsedTimeLimit) || parsedTimeLimit < 1 || parsedTimeLimit > 180) {
      newErrors.timeLimit = 'Time limit must be a number between 1 and 180 minutes.';
    }
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateForm()) {
      setMessage('');
      return;
    }

try {
const newQuiz = { title, description, timeLimit: parseInt(timeLimit) };
await axios.post('http://localhost:8080/api/quizzes', newQuiz);
setMessage('Quiz created successfully!');
setTitle('');
setDescription('');
setTimeLimit('');
setErrors({});
onQuizCreated();
} catch (err) {
// Access errors property from the mocked response
// const apiErrors = err.response?.data?.errors || ['An unexpected error occurred.'];
const apiErrors="Server error occurred";
setMessage(Array.isArray(apiErrors) ? apiErrors.join(', ') : apiErrors.toString());
}
};

return (
<div style={{
backgroundColor: 'white',
borderRadius: '12px',
padding: '2rem',
boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
margin: '1rem 0'
}}>
<h2 style={{
color: '#1f2937',
fontSize: '1.5rem',
fontWeight: '700',
marginBottom: '1.5rem',
textAlign: 'center'
}}>Create New Quiz</h2>
<form onSubmit={handleSubmit}>
<div style={{ marginBottom: '1.5rem' }}>
<label htmlFor="title-input" style={{
display: 'block',
fontWeight: '600',
color: '#374151',
marginBottom: '0.5rem'
}}>Title</label>
<input
type="text"
id="title-input"
value={title}
onChange={(e) => setTitle(e.target.value)}
placeholder="Enter quiz title"
style={{
width: '100%',
padding: '0.75rem',
border: '2px solid #d1d5db',
borderRadius: '8px',
fontSize: '1rem',
outline: 'none',
transition: 'border-color 0.2s',
boxSizing: 'border-box'
}}
onFocus={(e) => e.target.style.borderColor = '#3b82f6'}
onBlur={(e) => e.target.style.borderColor = '#d1d5db'}
/>
{errors.title && <span style={{ color: '#ef4444', fontSize: '0.875rem' }}>{errors.title}</span>}
</div>
<div style={{ marginBottom: '1.5rem' }}>
<label htmlFor="description-input" style={{
display: 'block',
fontWeight: '600',
color: '#374151',
marginBottom: '0.5rem'
}}>Description</label>
<textarea
id="description-input"
value={description}
onChange={(e) => setDescription(e.target.value)}
placeholder="Enter quiz description (optional)"
rows={3}
style={{
width: '100%',
padding: '0.75rem',
border: '2px solid #d1d5db',
borderRadius: '8px',
fontSize: '1rem',
outline: 'none',
transition: 'border-color 0.2s',
boxSizing: 'border-box',
resize: 'vertical'
}}
onFocus={(e) => e.target.style.borderColor = '#3b82f6'}
onBlur={(e) => e.target.style.borderColor = '#d1d5db'}
/>
{errors.description && <span style={{ color: '#ef4444', fontSize: '0.875rem' }}>{errors.description}</span>}
</div>
<div style={{ marginBottom: '1.5rem' }}>
<label htmlFor="timeLimit" style={{
display: 'block',
fontWeight: '600',
color: '#374151',
marginBottom: '0.5rem'
}}>Time Limit (minutes)</label>
<input
type="number"
id="timeLimit"
value={timeLimit}
onChange={(e) => setTimeLimit(e.target.value)}
placeholder="Enter time limit (1-180 minutes)"
style={{
width: '100%',
padding: '0.75rem',
border: '2px solid #d1d5db',
borderRadius: '8px',
fontSize: '1rem',
outline: 'none',
transition: 'border-color 0.2s',
boxSizing: 'border-box'
}}
onFocus={(e) => e.target.style.borderColor = '#3b82f6'}
onBlur={(e) => e.target.style.borderColor = '#d1d5db'}
/>
{errors.timeLimit && <span style={{ color: '#ef4444', fontSize: '0.875rem' }}>{errors.timeLimit}</span>}
</div>
<button type="submit" style={{
width: '100%',
padding: '0.875rem',
backgroundColor: '#3b82f6',
color: 'white',
border: 'none',
borderRadius: '8px',
fontSize: '1rem',
fontWeight: '600',
cursor: 'pointer',
transition: 'background-color 0.2s'
}}
onMouseOver={(e) => e.target.style.backgroundColor = '#2563eb'}
onMouseOut={(e) => e.target.style.backgroundColor = '#3b82f6'}
>Create Quiz</button>
</form>
{message && <p style={{
marginTop: '1rem',
padding: '0.75rem',
backgroundColor: message.includes('successfully') ? '#dcfce7' : '#fef2f2',
color: message.includes('successfully') ? '#065f46' : '#dc2626',
borderRadius: '6px',
textAlign: 'center'
}}>{message}</p>}
</div>
);
};

export default QuizForm;
