import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate, useLocation } from 'react-router-dom';
import './App.css'; // Assuming this file exists and is correctly linked
import './components/Navigation.css';
import QuizList from './components/QuizList';
import QuizForm from './components/QuizForm';
import QuestionForm from './components/QuestionForm';
import TakeQuiz from './components/TakeQuiz';
import QuizResults from './components/QuizResults';
import QuizViewQuestions from './components/QuizViewQuestions';
import Login from './components/Login';
import StudentResults from './components/StudentResults';
import Leaderboard from './components/Leaderboard';
import EnhancedStudentDashboard from './components/EnhancedStudentDashboard';
import EmailService from './components/EmailService';
import axios from 'axios';

function AppWithRouter() {
  const navigate = useNavigate();
  const location = useLocation();
  const [selectedQuizId, setSelectedQuizId] = useState(null);
  const [attemptResult, setAttemptResult] = useState(null);
  // Initial state for login flow
  const [view, setView] = useState('login'); 
  // Only student role supported in frontend
  const [userRole, setUserRole] = useState('student');
  const [isAuthenticated, setIsAuthenticated] = useState(false); // Start with login

  // Check for existing session on app load
  useEffect(() => {
    // Always start at login - removed auto-authentication
    // Initialize EmailJS
    EmailService.initialize();
  }, []);

  useEffect(() => {
    if (!isAuthenticated) {
      setView('login');
      if (location.pathname !== '/login') navigate('/login');
      return;
    }

    const path = location.pathname;
    if (path === '/') {
      if (userRole) {
        const dashboardView = userRole === 'admin' ? 'admin-dashboard' : 'student-dashboard';
        setView(dashboardView);
        navigate(userRole === 'admin' ? '/admin' : '/student');
      } else {
        setView('login');
        navigate('/login');
      }
    }
    else if (path === '/login') {
      if (userRole) {
        const dashboardView = userRole === 'admin' ? 'admin-dashboard' : 'student-dashboard';
        setView(dashboardView);
        navigate(userRole === 'admin' ? '/admin' : '/student');
      } else {
        setView('login');
      }
    }

    else if (path === '/student') setView('student-dashboard');
    else if (path === '/my-results') setView('my-results');
    else if (path === '/student-leaderboard') setView('student-leaderboard');
    else if (path.startsWith('/take-quiz/')) {
      setView('take-quiz');
      setSelectedQuizId(path.split('/')[2]);
    }
    else if (path === '/results') setView('results');
    else if (path.startsWith('/view-questions/')) {
      setView('view-questions');
      setSelectedQuizId(path.split('/')[2]);
    }
  }, [location.pathname, isAuthenticated, userRole, navigate]); 

  // Handler for login - only student access
  const handleLogin = () => {
    setIsAuthenticated(true);
    setUserRole('student');
    localStorage.setItem('isAuthenticated', 'true');
    localStorage.setItem('userRole', 'student');
    setView('student-dashboard');
    navigate('/student');
  };

  // Handler for logout
  const handleLogout = () => {
    setIsAuthenticated(false);
    setUserRole(null);
    localStorage.removeItem('isAuthenticated');
    localStorage.removeItem('userRole');
    setView('login');
    navigate('/login');
  };



// Handler for students to take a quiz
const handleStudentSelectQuiz = (quizId) => {
setSelectedQuizId(quizId);
setView('take-quiz');
navigate(`/take-quiz/${quizId}`);
};

// Handler for when a student completes a quiz
const handleQuizCompleted = (result) => {
setAttemptResult(result);
setView('results'); // Show quiz results screen
navigate('/results');
};

const handleReturnHome = () => {
setView('student-dashboard');
navigate('/student');
setSelectedQuizId(null);
setAttemptResult(null);
};

// New navigation handlers
const handleViewMyResults = () => {
setView('my-results');
navigate('/my-results');
};

const handleStudentLeaderboard = () => {
setView('student-leaderboard');
navigate('/student-leaderboard');
};






// Renders content based on the current 'view' state
const renderContent = () => {
if (!isAuthenticated) {
return <Login onLogin={handleLogin} />;
}

switch (view) {
case 'login':
return <Login onLogin={handleLogin} />;
// Role selection removed - student only

// Admin dashboard moved to backend
case 'student-dashboard':
return (
<EnhancedStudentDashboard
onSelectQuiz={handleStudentSelectQuiz}
onViewMyResults={handleViewMyResults}
onLeaderboard={handleStudentLeaderboard}
/>
);
case 'take-quiz':
return <TakeQuiz quizId={selectedQuizId} onQuizCompleted={handleQuizCompleted} />;
case 'results':
return <QuizResults attempt={attemptResult} onReturnHome={handleReturnHome} />;
case 'view-questions':
return <QuizViewQuestions quizId={selectedQuizId} onReturnHome={handleReturnHome} />;
case 'my-results':
return <StudentResults />;
case 'student-leaderboard':
return <Leaderboard onBack={handleReturnHome} />;
default:
return null;
}
};

return (
<div className="App">
{/* Hide header during quiz-taking */}
{view !== 'take-quiz' && (
<header className="App-header" style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
<h1 style={{ margin: 0 }}>QuizMaster</h1>
{isAuthenticated && (
<div style={{ display: 'flex', gap: '1rem' }}>
{/* Show 'Return to Home' button if not on the 'home' screen */}
{view !== 'home' && view !== 'login' && view !== 'admin-dashboard' && view !== 'student-dashboard' && (
<button onClick={handleReturnHome} className="home-button" style={{ padding: '0.5rem 1rem', borderRadius: '4px', border: 'none', backgroundColor: '#0056b3', color: 'white', cursor: 'pointer' }}>Return to Home</button>
)}

<button onClick={handleLogout} className="logout-button" style={{ padding: '0.5rem 1rem', borderRadius: '4px', border: 'none', backgroundColor: '#dc3545', color: 'white', cursor: 'pointer' }}>Logout</button>
</div>
)}
</header>
)}
<div className="main-content" style={{ marginTop: view === 'take-quiz' ? '0' : 'auto' }}>
{renderContent()}
</div>
</div>
);
}

function AppWithoutRouter() {
  const [selectedQuizId, setSelectedQuizId] = useState(null);
  const [quizListKey, setQuizListKey] = useState(0);
  const [attemptResult, setAttemptResult] = useState(null);
  const [view, setView] = useState('admin-dashboard');
  const [userRole, setUserRole] = useState('admin');
  const [isAuthenticated, setIsAuthenticated] = useState(true); // Keep true for test compatibility

  const handleLogin = (role) => {
    setIsAuthenticated(true);
    setUserRole(role);
    setView(role === 'admin' ? 'admin-dashboard' : 'student-dashboard');
  };

  const handleLogout = () => {
    setIsAuthenticated(false);
    setUserRole(null);
    setView('login');
  };

  const handleRoleSelection = (role) => {
    setUserRole(role);
    setView(role === 'admin' ? 'admin-dashboard' : 'student-dashboard');
  };

  const handleQuizCreated = () => {
    setQuizListKey(prevKey => prevKey + 1);
    setSelectedQuizId(null);
    setView('admin-dashboard');
  };

  const handleQuestionAdded = () => {
    if (view === 'view-questions' && selectedQuizId) {
      setQuizListKey(prevKey => prevKey + 1);
    } else {
      setView('admin-dashboard');
    }
  };

  const handleAdminSelectQuiz = (quizId) => {
    setSelectedQuizId(quizId);
    setView('admin-dashboard');
  };

  const handleViewQuestions = (quizId) => {
    setSelectedQuizId(quizId);
    setView('view-questions');
  };

  const handleStudentSelectQuiz = (quizId) => {
    setSelectedQuizId(quizId);
    setView('take-quiz');
  };

  const handleQuizCompleted = (result) => {
    setAttemptResult(result);
    setView('results');
  };

  const handleReturnHome = () => {
    setQuizListKey(prevKey => prevKey + 1);
    setView('home');
    setSelectedQuizId(null);
    setAttemptResult(null);
    setUserRole(null);
  };



  const handleViewMyResults = () => setView('my-results');

  const handleDeleteQuiz = (quizId) => {
    const quizzes = JSON.parse(localStorage.getItem('quizzes') || '[]');
    const updatedQuizzes = quizzes.filter(quiz => quiz.id !== quizId);
    localStorage.setItem('quizzes', JSON.stringify(updatedQuizzes));
    setQuizListKey(prevKey => prevKey + 1);
  };

  const renderContent = () => {
    if (!isAuthenticated) {
      return <Login onLogin={handleLogin} />;
    }

    switch (view) {
      case 'login':
        return <Login onLogin={handleLogin} />;
      case 'home':
        return (
          <div className="role-selection-container">
            <h2>Select Your Role</h2>
            <button onClick={() => handleRoleSelection('admin')} className="role-button admin-button">Admin</button>
            <button onClick={() => handleRoleSelection('student')} className="role-button student-button">Student</button>
          </div>
        );
      case 'admin-dashboard':
        return (
          <>
            <div className="admin-nav">
              <p>Admin functions moved to backend at <a href="http://localhost:8080/admin">localhost:8080/admin</a></p>
            </div>
            <div className="left-panel">
              <QuizList
                key={quizListKey}
                onSelectQuiz={handleAdminSelectQuiz}
                onViewQuestions={handleViewQuestions}
                onDeleteQuiz={handleDeleteQuiz}
                userRole={userRole}
              />
            </div>
            <div className="right-panel">
              <QuizForm onQuizCreated={handleQuizCreated} />
              {selectedQuizId && (
                <QuestionForm quizId={selectedQuizId} onQuestionAdded={handleQuestionAdded} />
              )}
            </div>
          </>
        );
      case 'student-dashboard':
        return (
          <>
            <div className="student-nav">
              <button onClick={handleViewMyResults}>My Results</button>
            </div>
            <div className="full-width-panel">
              <QuizList
                key={quizListKey}
                onSelectQuiz={handleStudentSelectQuiz}
                onViewQuestions={() => {}}
                userRole={userRole}
              />
            </div>
          </>
        );
      case 'take-quiz':
        return <TakeQuiz quizId={selectedQuizId} onQuizCompleted={handleQuizCompleted} />;
      case 'results':
        return <QuizResults attempt={attemptResult} onReturnHome={handleReturnHome} />;
      case 'view-questions':
        return <QuizViewQuestions quizId={selectedQuizId} onReturnHome={handleReturnHome} />;
      case 'my-results':
        return <StudentResults />;

      default:
        return null;
    }
  };

  return (
    <div className="App">
      <header className="App-header" style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <h1 style={{ margin: 0 }}>QuizMaster</h1>
        {isAuthenticated && (
          <div style={{ display: 'flex', gap: '1rem' }}>
            {view !== 'home' && view !== 'login' && view !== 'admin-dashboard' && view !== 'student-dashboard' && (
              <button onClick={handleReturnHome} className="home-button" style={{ padding: '0.5rem 1rem', borderRadius: '4px', border: 'none', backgroundColor: '#0056b3', color: 'white', cursor: 'pointer' }}>Return to Home</button>
            )}

            <button onClick={handleLogout} className="logout-button" style={{ padding: '0.5rem 1rem', borderRadius: '4px', border: 'none', backgroundColor: '#dc3545', color: 'white', cursor: 'pointer' }}>Logout</button>
          </div>
        )}
      </header>
      <div className="main-content">
        {renderContent()}
      </div>
    </div>
  );
}

function App() {
  const isTest = typeof process !== 'undefined' && process.env.NODE_ENV === 'test';

  if (isTest) {
    return <AppWithoutRouter />;
  }

  // For browser, start at login page
  return (
    <Router>
      <Routes>
        <Route path="/*" element={<AppWithRouter />} />
      </Routes>
    </Router>
  );
}

export default App;
