import React, { useState, useEffect } from 'react';
import EmailService from './EmailService';
import './ModernUI.css';

const EmailNotifications = () => {
  const [emailHistory, setEmailHistory] = useState([]);
  const [selectedQuiz, setSelectedQuiz] = useState('');
  const [quizzes, setQuizzes] = useState([]);
  const [students, setStudents] = useState([]);
  const [sending, setSending] = useState(false);
  const [message, setMessage] = useState('');

  useEffect(() => {
    loadData();
  }, []);

  const loadData = () => {
    const savedQuizzes = JSON.parse(localStorage.getItem('quizzes') || '[]');
    const savedStudents = JSON.parse(localStorage.getItem('students') || '[]');
    const history = EmailService.getEmailHistory();

    setQuizzes(savedQuizzes);
    setStudents(savedStudents);
    setEmailHistory(history);
  };

  const sendQuizReminders = async () => {
    if (!selectedQuiz) {
      setMessage('Please select a quiz first');
      return;
    }

    setSending(true);
    setMessage('Sending reminders...');

    const quiz = quizzes.find(q => q.id.toString() === selectedQuiz);
    const studentsWithEmail = students.filter(student => student.email);

    if (studentsWithEmail.length === 0) {
      setMessage('No students with email addresses found');
      setSending(false);
      return;
    }

    const emailPromises = studentsWithEmail.map(student => 
      EmailService.sendQuizReminder(student.email, quiz)
    );

    try {
      const results = await Promise.allSettled(emailPromises);
      const successful = results.filter(r => r.status === 'fulfilled' && r.value.success).length;
      setMessage(`Sent ${successful} reminder emails successfully!`);
      loadData(); // Refresh email history
    } catch (error) {
      setMessage('Failed to send reminders');
    } finally {
      setSending(false);
    }
  };

  const sendNewQuizNotification = async () => {
    if (!selectedQuiz) {
      setMessage('Please select a quiz first');
      return;
    }

    setSending(true);
    setMessage('Sending notifications...');

    const quiz = quizzes.find(q => q.id.toString() === selectedQuiz);

    try {
      const results = await EmailService.sendNewQuizNotification(quiz);
      const successful = results.filter(r => r.status === 'fulfilled' && r.value.success).length;
      setMessage(`Sent ${successful} new quiz notifications!`);
      loadData();
    } catch (error) {
      setMessage('Failed to send notifications');
    } finally {
      setSending(false);
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleString();
  };

  const getEmailTypeIcon = (type) => {
    switch (type) {
      case 'reminder': return '⏰';
      case 'results': return '📊';
      case 'new_quiz': return '📢';
      default: return '📧';
    }
  };

  const getEmailTypeLabel = (type) => {
    switch (type) {
      case 'reminder': return 'Quiz Reminder';
      case 'results': return 'Quiz Results';
      case 'new_quiz': return 'New Quiz';
      default: return 'Email';
    }
  };

  return (
    <div className="modern-card">
      <h2 style={{ color: 'var(--text-primary)', marginBottom: '1.5rem' }}>
        📧 Email Notifications
      </h2>

      {/* Manual Email Controls */}
      <div style={{ marginBottom: '2rem' }}>
        <h3 style={{ color: 'var(--text-primary)', marginBottom: '1rem' }}>📤 Send Manual Emails</h3>

        <div className="modern-form-group">
          <label className="modern-form-label">Select Quiz:</label>
          <select 
            className="modern-form-select"
            value={selectedQuiz}
            onChange={(e) => setSelectedQuiz(e.target.value)}
          >
            <option value="">Choose a quiz...</option>
            {quizzes.map(quiz => (
              <option key={quiz.id} value={quiz.id}>{quiz.title}</option>
            ))}
          </select>
        </div>

        <div style={{ display: 'flex', gap: '1rem', flexWrap: 'wrap' }}>
          <button 
            className="modern-btn modern-btn-primary"
            onClick={sendQuizReminders}
            disabled={sending || !selectedQuiz}
            title="Send reminder emails to all students for the selected quiz"
          >
            {sending ? '⏳ Sending...' : '⏰ Send Reminder'}
          </button>
        </div>

        {message && (
          <div style={{
            marginTop: '1rem',
            padding: '0.75rem',
            borderRadius: '6px',
            backgroundColor: message.includes('Failed') ? '#fee2e2' : '#d1fae5',
            color: message.includes('Failed') ? '#dc2626' : '#065f46',
            border: `1px solid ${message.includes('Failed') ? '#fecaca' : '#a7f3d0'}`
          }}>
            {message}
          </div>
        )}
      </div>

      {/* Email Statistics */}
      <div style={{ marginBottom: '2rem' }}>
        <h3 style={{ color: 'var(--text-primary)' }}>📊 Email Statistics</h3>
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(120px, 1fr))', gap: '1rem' }}>
          <div style={{ 
            padding: '1rem', 
            backgroundColor: '#eff6ff', 
            borderRadius: '8px',
            textAlign: 'center'
          }}>
            <div style={{ fontSize: '1.5rem', fontWeight: 'bold', color: '#1e40af' }}>
              {emailHistory.filter(e => e.type === 'reminder').length}
            </div>
            <div style={{ fontSize: '0.875rem', color: '#64748b' }}>⏰ Reminders</div>
          </div>

          <div style={{ 
            padding: '1rem', 
            backgroundColor: '#f0fdf4', 
            borderRadius: '8px',
            textAlign: 'center'
          }}>
            <div style={{ fontSize: '1.5rem', fontWeight: 'bold', color: '#16a34a' }}>
              {emailHistory.filter(e => e.type === 'results').length}
            </div>
            <div style={{ fontSize: '0.875rem', color: '#64748b' }}>📊 Results</div>
          </div>

          <div style={{ 
            padding: '1rem', 
            backgroundColor: '#fef3c7', 
            borderRadius: '8px',
            textAlign: 'center'
          }}>
            <div style={{ fontSize: '1.5rem', fontWeight: 'bold', color: '#d97706' }}>
              {emailHistory.length}
            </div>
            <div style={{ fontSize: '0.875rem', color: '#64748b' }}>📧 Total</div>
          </div>

          <div style={{ 
            padding: '1rem', 
            backgroundColor: '#f3e8ff', 
            borderRadius: '8px',
            textAlign: 'center'
          }}>
            <div style={{ fontSize: '1.5rem', fontWeight: 'bold', color: '#7c3aed' }}>
              {students.filter(s => s.email).length}
            </div>
            <div style={{ fontSize: '0.875rem', color: '#64748b' }}>👥 Students</div>
          </div>
        </div>
      </div>

      {/* Separate Tables for Reminders and Results */}
      <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '2rem' }}>

        {/* Reminder Emails Table */}
        <div>
          <h3 style={{ color: 'var(--text-primary)', marginBottom: '1rem' }}>⏰ Reminder Emails</h3>
          <div style={{ 
            backgroundColor: '#f8fafc', 
            borderRadius: '8px', 
            border: '1px solid #e2e8f0',
            maxHeight: '300px',
            overflowY: 'auto'
          }}>
            {emailHistory.filter(e => e.type === 'reminder').length === 0 ? (
              <p style={{ color: 'var(--text-secondary)', textAlign: 'center', padding: '2rem' }}>
                No reminder emails sent yet
              </p>
            ) : (
              emailHistory.filter(e => e.type === 'reminder').slice(-10).reverse().map((email, index) => (
                <div key={index} style={{
                  display: 'flex',
                  alignItems: 'center',
                  padding: '0.75rem',
                  borderBottom: '1px solid #e2e8f0'
                }}>
                  <span style={{ fontSize: '1.2rem', marginRight: '0.75rem' }}>⏰</span>
                  <div style={{ flex: 1 }}>
                    <div style={{ fontWeight: '500', color: 'var(--text-primary)', fontSize: '0.875rem' }}>
                      {email.email}
                    </div>
                    <div style={{ fontSize: '0.75rem', color: 'var(--text-secondary)' }}>
                      {formatDate(email.timestamp)}
                    </div>
                  </div>
                  <span style={{
                    padding: '0.25rem 0.5rem',
                    borderRadius: '12px',
                    fontSize: '0.75rem',
                    backgroundColor: '#dbeafe',
                    color: '#1e40af'
                  }}>
                    sent
                  </span>
                </div>
              ))
            )}
          </div>
        </div>

        {/* Result Emails Table */}
        <div>
          <h3 style={{ color: 'var(--text-primary)', marginBottom: '1rem' }}>📊 Result Emails</h3>
          <div style={{ 
            backgroundColor: '#f8fafc', 
            borderRadius: '8px', 
            border: '1px solid #e2e8f0',
            maxHeight: '300px',
            overflowY: 'auto'
          }}>
            {emailHistory.filter(e => e.type === 'results').length === 0 ? (
              <p style={{ color: 'var(--text-secondary)', textAlign: 'center', padding: '2rem' }}>
                No result emails sent yet
              </p>
            ) : (
              emailHistory.filter(e => e.type === 'results').slice(-10).reverse().map((email, index) => (
                <div key={index} style={{
                  display: 'flex',
                  alignItems: 'center',
                  padding: '0.75rem',
                  borderBottom: '1px solid #e2e8f0'
                }}>
                  <span style={{ fontSize: '1.2rem', marginRight: '0.75rem' }}>📊</span>
                  <div style={{ flex: 1 }}>
                    <div style={{ fontWeight: '500', color: 'var(--text-primary)', fontSize: '0.875rem' }}>
                      {email.email}
                    </div>
                    <div style={{ fontSize: '0.75rem', color: 'var(--text-secondary)' }}>
                      {formatDate(email.timestamp)}
                    </div>
                  </div>
                  <span style={{
                    padding: '0.25rem 0.5rem',
                    borderRadius: '12px',
                    fontSize: '0.75rem',
                    backgroundColor: '#dcfce7',
                    color: '#166534'
                  }}>
                    sent
                  </span>
                </div>
              ))
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default EmailNotifications;
