import React, { useState } from 'react';
import axios from 'axios';

const QuizForm = ({ onQuizCreated }) => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [timeLimit, setTimeLimit] = useState('');
  const [message, setMessage] = useState('');
  const [errors, setErrors] = useState({});

  const validateForm = () => {
    const newErrors = {};
    if (!title || title.length < 3 || title.length > 100) {
      newErrors.title = 'Quiz title must be between 3 and 100 characters.';
    }

    const parsedTimeLimit = parseInt(timeLimit);
    if (isNaN(parsedTimeLimit) || parsedTimeLimit < 1 || parsedTimeLimit > 180) {
      newErrors.timeLimit = 'Time limit must be a number between 1 and 180 minutes.';
    }
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateForm()) {
      setMessage('');
      return;
    }

try {
const newQuiz = { title, description, timeLimit: parseInt(timeLimit) };
await axios.post('http://localhost:8080/api/quizzes', newQuiz);
setMessage('Quiz created successfully!');
setTitle('');
setDescription('');
setTimeLimit('');
setErrors({});
onQuizCreated();
} catch (err) {
// Access errors property from the mocked response
// const apiErrors = err.response?.data?.errors || ['An unexpected error occurred.'];
const apiErrors="Server error occurred";
setMessage(Array.isArray(apiErrors) ? apiErrors.join(', ') : apiErrors.toString());
}
};

return (
<div style={{ backgroundColor: '#f8fafc', padding: '1rem', borderRadius: '8px' }}>
<h2 style={{ color: '#1f2937', marginBottom: '1rem' }}>Create New Quiz</h2>
<form onSubmit={handleSubmit}>
<div style={{ marginBottom: '1rem' }}>
<label htmlFor="title-input" style={{ display: 'block', fontWeight: '500', marginBottom: '0.25rem' }}>Title</label>
<input
type="text"
id="title-input"
value={title}
onChange={(e) => setTitle(e.target.value)}
placeholder="Enter quiz title"
style={{
width: '100%',
padding: '0.5rem',
border: '1px solid #d1d5db',
borderRadius: '4px',
backgroundColor: 'white'
}}
/>
{errors.title && <span style={{ color: '#ef4444', fontSize: '0.875rem' }}>{errors.title}</span>}
</div>
<div style={{ marginBottom: '1rem' }}>
<label htmlFor="description-input" style={{ display: 'block', fontWeight: '500', marginBottom: '0.25rem' }}>Description</label>
<textarea
id="description-input"
value={description}
onChange={(e) => setDescription(e.target.value)}
placeholder="Enter quiz description (optional)"
rows={3}
style={{
width: '100%',
padding: '0.5rem',
border: '1px solid #d1d5db',
borderRadius: '4px',
backgroundColor: 'white'
}}
/>
{errors.description && <span style={{ color: '#ef4444', fontSize: '0.875rem' }}>{errors.description}</span>}
</div>
<div style={{ marginBottom: '1rem' }}>
<label htmlFor="timeLimit" style={{ display: 'block', fontWeight: '500', marginBottom: '0.25rem' }}>Time Limit (minutes)</label>
<input
type="number"
id="timeLimit"
value={timeLimit}
onChange={(e) => setTimeLimit(e.target.value)}
placeholder="Enter time limit (1-180 minutes)"
style={{
width: '100%',
padding: '0.5rem',
border: '1px solid #d1d5db',
borderRadius: '4px',
backgroundColor: 'white'
}}
/>
{errors.timeLimit && <span style={{ color: '#ef4444', fontSize: '0.875rem' }}>{errors.timeLimit}</span>}
</div>
<button type="submit" style={{
padding: '0.5rem 1rem',
backgroundColor: '#3b82f6',
color: 'white',
border: 'none',
borderRadius: '4px',
cursor: 'pointer'
}}>Create Quiz</button>
</form>
{message && <p style={{ marginTop: '0.5rem', color: message.includes('successfully') ? '#059669' : '#dc2626' }}>{message}</p>}
</div>
);
};

export default QuizForm;
