import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';

const TakeQuiz = ({ quizId, onQuizCompleted }) => {
    const [quiz, setQuiz] = useState(null);
    const [questions, setQuestions] = useState([]);
    const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
    const [answers, setAnswers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [timeLeft, setTimeLeft] = useState(0);
    const [startTime, setStartTime] = useState(null);
    const [studentName] = useState(() => {
        const username = localStorage.getItem('username') || localStorage.getItem('studentName') || 'Student';
        console.log('Current username from localStorage:', username);
        console.log('All localStorage keys:', Object.keys(localStorage));
        return username;
    });

    // Your exact Examly BASE_URL, as provided.
    // This constant is still defined for clarity, but its value is now directly used below.
    useEffect(() => {
        const fetchQuiz = async () => {
            try {
                setLoading(true);

                // FIX: Your URL is now directly included in the axios.get calls
                const quizResponse = await axios.get(`http://localhost:8080/api/quizzes/${quizId}`);
                setQuiz(quizResponse.data);
                const questionsResponse = await axios.get(`http://localhost:8080/api/quizzes/${quizId}/questions`);

                // Get edited questions from localStorage
                const editedQuestions = JSON.parse(localStorage.getItem('editedQuestions') || '{}');
                console.log('Edited questions available:', editedQuestions);

                // Use edited versions if available, otherwise use API data
                const finalQuestions = questionsResponse.data.map(q => {
                    if (editedQuestions[q.id]) {
                        console.log('Using edited version for question:', q.id);
                        return editedQuestions[q.id];
                    }
                    return q;
                });

                console.log('Final questions for quiz:', finalQuestions);
                setQuestions(finalQuestions);
                setTimeLeft(quizResponse.data.timeLimit * 60);
                setStartTime(Date.now());
            } catch (err) {
                const apiErrors = err.response?.data?.errors || ['Failed to load quiz. Please check backend and URL.'];
                setError(Array.isArray(apiErrors) ? apiErrors.join(', ') : apiErrors.toString());
                console.error('Error fetching quiz or questions:', err);
            } finally {
                setLoading(false);
            }
        };
        // Removed EXAMLY_BASE_URL_STRING from dependencies as it's now hardcoded in the URL
        fetchQuiz();
    }, [quizId]);

    const handleSubmitQuiz = useCallback(async () => {
try {
const submission = {
quizId: quiz.id,
studentName: studentName,
answers
};
console.log('Submitting quiz with data:', submission);

// Calculate score
const correctAnswers = answers.filter(answer => {
const question = questions.find(q => q.id === answer.questionId);
const selectedOption = question?.options.find(opt => opt.id === answer.selectedOptionId);
return selectedOption?.isCorrect;
}).length;

const score = Math.round((correctAnswers / questions.length) * 100);
const timeTakenSeconds = Math.floor((Date.now() - startTime) / 1000);
const timeTakenFormatted = `${Math.floor(timeTakenSeconds / 60)}:${(timeTakenSeconds % 60).toString().padStart(2, '0')}`;

// Create result object
const quizResult = {
id: Date.now(),
quizTitle: quiz.title,
studentName: studentName,
score: score,
correctAnswers: correctAnswers,
totalQuestions: questions.length,
completedAt: new Date().toISOString(),
timeTaken: timeTakenFormatted
};

// Always save to localStorage for local display
const existingResults = JSON.parse(localStorage.getItem('quizResults') || '[]');
existingResults.push(quizResult);
localStorage.setItem('quizResults', JSON.stringify(existingResults));

// Also save to quizAttempts for admin dashboard
const existingAttempts = JSON.parse(localStorage.getItem('quizAttempts') || '[]');
existingAttempts.push(quizResult);
localStorage.setItem('quizAttempts', JSON.stringify(existingAttempts));

// CRITICAL: Clear retake permission immediately
localStorage.setItem('retakePermissions', '[]');

// Force immediate dashboard refresh
window.dispatchEvent(new Event('quizSubmitted'));
window.dispatchEvent(new Event('retakePermissionUpdated'));

console.log('Saved to localStorage. All results:', existingResults);

try {
// Try API as well
const result = await axios.post(`http://localhost:8080/api/quiz-attempts`, submission);
console.log('Quiz submission successful:', result.data);
onQuizCompleted(result.data);
} catch (apiErr) {
console.log('API failed, but localStorage saved:', apiErr);
onQuizCompleted(quizResult);
}
} catch (err) {
const apiErrors = err.response?.data?.errors || ['Failed to submit quiz. Please try again.'];
setError(Array.isArray(apiErrors) ? apiErrors.join(', ') : apiErrors.toString());
console.error('Error submitting quiz:', err);
}
}, [quiz, studentName, answers, questions, startTime, onQuizCompleted]);

    useEffect(() => {
        if (timeLeft > 0 && !loading) {
            const timer = setInterval(() => {
                setTimeLeft(prev => {
                    if (prev <= 1) {
                        handleSubmitQuiz();
                        return 0;
                    }
                    return prev - 1;
                });
            }, 1000);
            return () => clearInterval(timer);
        }
    }, [timeLeft, loading, handleSubmitQuiz]); 

    const handleAnswerSelect = (optionId) => {
        const existingAnswer = answers.find(a => a.questionId === questions[currentQuestionIndex].id);

if (existingAnswer) {
setAnswers(answers.map(a =>
a.questionId === questions[currentQuestionIndex].id ? { ...a, selectedOptionId: optionId } : a
));
} else {
setAnswers([...answers, { questionId: questions[currentQuestionIndex].id, selectedOptionId: optionId }]);
}
};

const handleNextQuestion = () => {
setCurrentQuestionIndex(currentQuestionIndex + 1);
};

const handlePreviousQuestion = () => {
setCurrentQuestionIndex(currentQuestionIndex - 1);
};

if (loading) return <div className="loading-state">Loading quiz...</div>;
if (error) return <div className="error-state">Error: {error}</div>;
if (questions.length === 0) return <div className="empty-state">No questions available for this quiz.</div>;

const currentQuestion = questions[currentQuestionIndex];
const selectedOptionId = answers.find(a => a.questionId === currentQuestion.id)?.selectedOptionId;

return (
<div style={{ width: '100vw', height: '100vh', backgroundColor: '#f8fafc', margin: 0, padding: 0, overflow: 'hidden' }}>
{/* Header */}
<div style={{ 
display: 'flex', 
justifyContent: 'space-between', 
alignItems: 'center', 
height: '80px',
padding: '0 2rem',
backgroundColor: 'white',
borderBottom: '1px solid #e5e7eb'
}}>
<h1 style={{ margin: 0, fontSize: '1.5rem', fontWeight: '600', color: '#1f2937' }}>
{quiz.title}
</h1>
<div style={{ 
fontSize: '1.25rem', 
fontWeight: '600', 
color: timeLeft < 300 ? '#dc2626' : '#059669',
backgroundColor: timeLeft < 300 ? '#fef2f2' : '#f0fdf4',
padding: '0.75rem 1.5rem',
borderRadius: '8px',
border: `2px solid ${timeLeft < 300 ? '#fecaca' : '#bbf7d0'}`
}}>
Time: {Math.floor(timeLeft / 60)}:{(timeLeft % 60).toString().padStart(2, '0')}
</div>
</div>

{/* Main Content - 3 Panel Layout */}
<div style={{ display: 'flex', height: 'calc(100vh - 80px)' }}>
{/* Left Panel - Question Numbers (10%) */}
<div style={{ 
width: '10%', 
height: '100%',
backgroundColor: 'white', 
padding: '1rem',
borderRight: '1px solid #e5e7eb',
overflowY: 'auto'
}}>
<h3 style={{ margin: '0 0 1rem 0', fontSize: '0.875rem', fontWeight: '600', color: '#374151', textAlign: 'center' }}>Questions</h3>
<div style={{ display: 'grid', gridTemplateColumns: '1fr', gap: '0.5rem' }}>
{questions.map((_, index) => {
const isAnswered = answers.some(a => a.questionId === questions[index].id);
return (
<button
key={index}
onClick={() => setCurrentQuestionIndex(index)}
style={{
padding: '0.75rem 0.5rem',
border: 'none',
borderRadius: '6px',
cursor: 'pointer',
fontWeight: '500',
fontSize: '0.875rem',
backgroundColor: currentQuestionIndex === index ? '#3b82f6' : isAnswered ? '#10b981' : '#e5e7eb',
color: currentQuestionIndex === index ? 'white' : isAnswered ? 'white' : '#374151'
}}
>
{index + 1}
</button>
);
})}
</div>
</div>

{/* Center Panel - Question (45%) */}
<div style={{ 
width: '45%', 
height: '100%',
backgroundColor: 'white', 
padding: '2rem',
borderRight: '1px solid #e5e7eb',
overflow: 'hidden'
}}>
<h2 style={{ 
margin: '0', 
fontSize: '1.25rem', 
fontWeight: '600', 
color: '#1f2937',
lineHeight: '1.6',
textDecoration: 'none'
}}>
{currentQuestion.questionText}
</h2>
</div>

{/* Right Panel - Options & Navigation (45%) */}
<div style={{ 
width: '45%', 
height: '100%',
backgroundColor: 'white', 
padding: '2rem',
display: 'flex',
flexDirection: 'column'
}}>
<div style={{ 
flex: 1, 
display: 'flex',
flexDirection: 'column'
}}>
<div style={{
flex: 1,
overflowY: 'auto',
marginBottom: '2rem'
}}>
{currentQuestion.options.map((option, index) => (
<label
key={option.id}
style={{
display: 'flex',
alignItems: 'center',
padding: '1rem',
marginBottom: '1rem',
border: '2px solid',
borderColor: selectedOptionId === option.id ? '#3b82f6' : '#e5e7eb',
borderRadius: '8px',
cursor: 'pointer',
backgroundColor: selectedOptionId === option.id ? '#eff6ff' : 'white',
transition: 'all 0.2s'
}}
>
<input
type="radio"
name={`question-${currentQuestion.id}`}
value={option.id}
checked={selectedOptionId === option.id}
onChange={() => handleAnswerSelect(option.id)}
style={{ marginRight: '1rem', transform: 'scale(1.2)' }}
/>
<span style={{ 
fontSize: '0.95rem', 
color: '#374151',
fontWeight: selectedOptionId === option.id ? '500' : '400'
}}>
{option.optionText}
</span>
</label>
))}
</div>

{/* Navigation Buttons */}
<div style={{ 
display: 'flex', 
gap: '1rem'
}}>
<button
onClick={handlePreviousQuestion}
disabled={currentQuestionIndex === 0}
style={{
flex: 1,
padding: '0.75rem 1.5rem',
border: 'none',
borderRadius: '8px',
fontWeight: '500',
cursor: currentQuestionIndex === 0 ? 'not-allowed' : 'pointer',
backgroundColor: currentQuestionIndex === 0 ? '#f3f4f6' : '#6b7280',
color: currentQuestionIndex === 0 ? '#9ca3af' : 'white'
}}
>
Previous
</button>

{currentQuestionIndex < questions.length - 1 ? (
<button
onClick={handleNextQuestion}
style={{
flex: 1,
padding: '0.75rem 1.5rem',
border: 'none',
borderRadius: '8px',
fontWeight: '500',
cursor: 'pointer',
backgroundColor: '#3b82f6',
color: 'white'
}}
>
Next
</button>
) : (
<button
onClick={handleSubmitQuiz}
style={{
flex: 1,
padding: '0.75rem 1.5rem',
border: 'none',
borderRadius: '8px',
fontWeight: '500',
cursor: 'pointer',
backgroundColor: '#dc2626',
color: 'white'
}}
>
Submit Quiz
</button>
)}
</div>
</div>
</div>
</div>
</div>
);
};

export default TakeQuiz;
