import React, { useState, useEffect } from 'react';
import './ModernUI.css';

const EnhancedStudentDashboard = ({ onSelectQuiz, onViewMyResults, onLeaderboard }) => {
  const [studentStats, setStudentStats] = useState({
    totalQuizzes: 0,
    completedQuizzes: 0,
    averageScore: 0,
    recentAttempts: []
  });
  const [availableQuizzes, setAvailableQuizzes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [studentName] = useState(() => localStorage.getItem('username') || 'Student');
  const [forceUpdate, setForceUpdate] = useState(0);


  // Check if student has already taken this quiz
  const hasStudentTakenQuiz = (quiz) => {
    const attempts = JSON.parse(localStorage.getItem('quizAttempts') || '[]');
    return attempts.some(attempt => 
      attempt.quizTitle === quiz.title && attempt.studentName === studentName
    );
  };

  // Check if student has retake permission
  const hasRetakePermission = (quiz) => {
    const permissions = JSON.parse(localStorage.getItem('retakePermissions') || '[]');
    return permissions.some(permission => 
      permission.quizTitle === quiz.title && permission.studentName === studentName
    );
  };



  useEffect(() => {
    loadStudentData();
  }, []);

  useEffect(() => {
    const handleQuestionsUpdated = () => {
      loadStudentData();
    };
    const handleRetakePermissionUpdated = () => {
      loadStudentData();
    };
    const handleQuizSubmitted = () => {
      loadStudentData();
      setForceUpdate(prev => prev + 1);
    };
    window.addEventListener('retakePermissionUpdated', handleRetakePermissionUpdated);
    window.addEventListener('quizSubmitted', handleQuizSubmitted);
    window.addEventListener('questionsUpdated', handleQuestionsUpdated);
    window.addEventListener('retakePermissionUpdated', handleRetakePermissionUpdated);
    return () => {
      window.removeEventListener('questionsUpdated', handleQuestionsUpdated);
      window.removeEventListener('retakePermissionUpdated', handleRetakePermissionUpdated);
      window.removeEventListener('quizSubmitted', handleQuizSubmitted);
    };
  }, []);

  const loadStudentData = async () => {
    try {
      const username = localStorage.getItem('username');

      // Load quizzes from localStorage (includes questions)
      const quizzes = JSON.parse(localStorage.getItem('quizzes') || '[]');

      const attempts = JSON.parse(localStorage.getItem('quizAttempts') || '[]');

      console.log('Loading student data:', { username, quizzes, attempts });

      // Filter attempts for current student
      const studentAttempts = attempts.filter(attempt => attempt.studentName === username);

      // Calculate stats
      const completedQuizIds = [...new Set(studentAttempts.map(attempt => attempt.quizId))];
      const totalScore = studentAttempts.reduce((sum, attempt) => sum + attempt.score, 0);
      const averageScore = studentAttempts.length > 0 ? Math.round(totalScore / studentAttempts.length) : 0;

      // Get recent attempts (last 5)
      const recentAttempts = studentAttempts
        .sort((a, b) => new Date(b.completedAt) - new Date(a.completedAt))
        .slice(0, 5);

      setStudentStats({
        totalQuizzes: quizzes.length,
        completedQuizzes: completedQuizIds.length,
        averageScore,
        recentAttempts
      });

      setAvailableQuizzes(quizzes);
      console.log('Set available quizzes:', quizzes);
      setLoading(false);
    } catch (error) {
      console.error('Error loading student data:', error);
      setLoading(false);
    }
  };



  if (loading) {
    return (
      <div className="modern-loading">
        <div className="modern-spinner"></div>
        Loading your dashboard...
      </div>
    );
  }



  return (
    <div className="enhanced-student-dashboard" style={{ display: 'flex', height: '100vh', width: '100%', overflow: 'hidden' }}>
      {/* Left Sidebar - Stats (20%) */}
      <div className="student-left-sidebar" style={{ 
        width: '20%', 
        backgroundColor: '#f8fafc', 
        padding: '1.5rem 1rem', 
        display: 'flex', 
        flexDirection: 'column', 
        gap: '1rem',
        borderRight: '1px solid #e5e7eb',
        overflow: 'hidden'
      }}>
        <div className="modern-stats-card" style={{ margin: 0 }}>
          <div className="modern-stats-number">{studentStats.totalQuizzes}</div>
          <div className="modern-stats-label">Available Quizzes</div>
        </div>
        <div className="modern-stats-card" style={{ background: 'linear-gradient(135deg, var(--success-color), var(--accent-color))', margin: 0 }}>
          <div className="modern-stats-number">{studentStats.completedQuizzes}</div>
          <div className="modern-stats-label">Completed Quizzes</div>
        </div>
        <div className="modern-stats-card" style={{ background: 'linear-gradient(135deg, var(--warning-color), var(--success-color))', margin: 0 }}>
          <div className="modern-stats-number">{studentStats.averageScore}%</div>
          <div className="modern-stats-label">Average Score</div>
        </div>
      </div>

      {/* Right Main Content (80%) */}
      <div className="student-main-content" style={{ 
        width: '80%', 
        padding: '1.5rem',
        overflowY: 'auto',
        height: '100vh'
      }}>
        {/* Welcome Section */}
        <div className="modern-card">
          <h2 style={{ color: 'var(--text-primary)', marginBottom: '0.5rem' }}>
            Welcome back, {localStorage.getItem('username')}!
          </h2>
          <p style={{ color: 'var(--text-secondary)', margin: 0 }}>
            Test your knowledge and track your progress with our interactive quizzes.
          </p>
        </div>

        {/* Available Quizzes */}
        <div className="modern-card">
          <h3 style={{ color: 'var(--text-primary)', marginBottom: '1rem' }}>
            Available Quizzes
          </h3>
          {availableQuizzes.length === 0 ? (
            <div className="modern-alert modern-alert-warning">
              No quizzes available at the moment. Check back later!
            </div>
          ) : (
            <div style={{ display: 'grid', gap: '1rem' }}>
              {availableQuizzes.map(quiz => (
                <div key={`${quiz.id}-${forceUpdate}`} className="modern-card" style={{ margin: 0, padding: '1rem', position: 'relative' }}>
                  <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start' }}>
                    <div style={{ flex: 1 }}>
                      <h4 style={{ color: 'var(--text-primary)', margin: '0 0 0.5rem 0', textAlign: 'left' }}>
                        {quiz.title}
                      </h4>
                      <p style={{ color: 'var(--text-secondary)', margin: '0 0 1rem 0', fontSize: '0.875rem', textAlign: 'left' }}>
                        {quiz.description}
                      </p>
                      <div style={{ display: 'flex', gap: '1rem', fontSize: '0.75rem', color: 'var(--text-secondary)' }}>
                        <span>⏱️ {quiz.timeLimit} minutes</span>
                      </div>
                    </div>
                    {hasStudentTakenQuiz(quiz) && !hasRetakePermission(quiz) ? (
                      <button 
                        className="modern-btn"
                        style={{ 
                          marginLeft: '1rem', 
                          backgroundColor: '#10b981', 
                          color: 'white', 
                          cursor: 'default' 
                        }}
                        disabled
                      >
                        Submitted
                      </button>
                    ) : (
                      <button 
                        className="modern-btn modern-btn-primary"
                        onClick={() => onSelectQuiz(quiz.id)}
                        style={{ marginLeft: '1rem' }}
                      >
                        {hasStudentTakenQuiz(quiz) ? 'Retake Quiz' : 'Start Quiz'}
                      </button>
                    )}
                  </div>
                </div>
              ))}
            </div>
          )}

          {/* Action Buttons */}
          <div style={{ marginTop: '1.5rem', paddingTop: '1.5rem', borderTop: '1px solid #e5e7eb', display: 'flex', gap: '1rem' }}>
            <button className="modern-btn" onClick={onViewMyResults} style={{ backgroundColor: '#dbeafe', color: '#1e40af', border: '1px solid #bfdbfe', padding: '1rem', fontSize: '1rem', flex: 1 }}>
              📊 View My Results
            </button>
            <button className="modern-btn" onClick={onLeaderboard} style={{ backgroundColor: '#fef3c7', color: '#92400e', border: '1px solid #fde68a', padding: '1rem', fontSize: '1rem', flex: 1 }}>
              🏆 Leaderboard
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default EnhancedStudentDashboard;
